INSERT INTO `site_settings_setting` (`name`, `label`, `description`, `data_type`, `value`, `default_value`, `input_type`, `input_value`, `client_editable`, `store`, `update_dt`, `updated_by`, `scope`, `scope_category`, `parent_id`) VALUES ('dateformat','Date Format','<div><strong>a</strong>&nbsp;&nbsp;\'a.m.\' or \'p.m.\' (Note that this is slightly different than PHP\'s output, because this includes periods to match Associated Press style.)</div>\n<div><strong>A</strong>&nbsp;&nbsp;\'AM\' or \'PM\'.</div>\n<div><strong>b</strong>&nbsp;&nbsp;Month, textual, 3 letters, lowercase.</div>\n<div><strong>B</strong>&nbsp;&nbsp;Not implemented.</div>\n<div><strong>c</strong>&nbsp;&nbsp;ISO 8601 Format.</div>\n<div><strong>d</strong>&nbsp;&nbsp;Day of the month, 2 digits with leading zeros.</div>\n<div><strong>D</strong>&nbsp;&nbsp;Day of the week, textual, 3 letters.</div>\n<div><strong>f</strong>&nbsp;&nbsp;Time, in 12-hour hours and minutes, with minutes left off if they\'re zero. Proprietary extension.</div>\n<div><strong>F</strong>&nbsp;&nbsp;Month, textual, long.</div>\n<div><strong>g</strong>&nbsp;&nbsp;Hour, 12-hour format without leading zeros.</div>\n<div><strong>G</strong>&nbsp;&nbsp;Hour, 24-hour format without leading zeros.</div>\n<div><strong>h</strong>&nbsp;&nbsp;Hour, 12-hour format.</div>\n<div><strong>H</strong>&nbsp;&nbsp;Hour, 24-hour format.</div>\n<div><strong>i</strong>&nbsp;&nbsp;Minutes.</div>\n<div><strong>I</strong>&nbsp;&nbsp;Not implemented.</div>\n<div><strong>j</strong>&nbsp;&nbsp;Day of the month without leading zeros.</div>\n<div><strong>l</strong>&nbsp;&nbsp;Day of the week, textual, long.</div>\n<div><strong>L</strong>&nbsp;&nbsp;Boolean for whether it\'s a leap year.</div>\n<div><strong>m</strong>&nbsp;&nbsp;Month, 2 digits with leading zeros.</div>\n<div><strong>M</strong>&nbsp;&nbsp;Month, textual, 3 letters.</div>\n<div><strong>n</strong>&nbsp;&nbsp;Month without leading zeros.</div>\n<div><strong>N</strong>&nbsp;&nbsp;Month abbreviation in Associated Press style. Proprietary extension.</div>\n<div><strong>O</strong>&nbsp;&nbsp;Difference to Greenwich time in hours.</div>\n<div><strong>P</strong>&nbsp;&nbsp;Time, in 12-hour hours, minutes and \'a.m.\'/\'p.m.\', with minutes left off if they\'re zero and the special-case strings \'midnight and \'noon\' if appropriate. Proprietary extension.</div>\n<div><strong>r</strong>&nbsp;&nbsp;RFC 2822 formatted date.</div>\n<div><strong>s</strong>&nbsp;&nbsp;Seconds, 2 digits with leading zeros.</div>\n<div><strong>S</strong>&nbsp;&nbsp;English ordinal suffix for day of the month, 2 characters.</div>\n<div><strong>t</strong>&nbsp;&nbsp;Number of days in the given month.</div>\n<div><strong>T</strong>&nbsp;&nbsp;Time zone of this machine.</div>\n<div><strong>u</strong>&nbsp;&nbsp;Microseconds.</div>\n<div><strong>U</strong>&nbsp;&nbsp;Seconds since the Unix Epoch (January 1 1970 00:00:00 UTC).</div>\n<div><strong>w</strong>&nbsp;&nbsp;Day of the week, digits without leading zeros.</div>\n<div><strong>W</strong>&nbsp;&nbsp;ISO-8601 week number of year, with weeks starting on Monday.</div>\n<div><strong>y</strong>&nbsp;&nbsp;Year, 2 digits.</div>\n<div><strong>Y</strong>&nbsp;&nbsp;Year, 4 digits.</div>\n<div><strong>z</strong>&nbsp;&nbsp;Day of the year.</div>\n<div><strong>Z</strong>&nbsp;&nbsp;Time zone offset in seconds. The offset for timezones west of UTC is always negative, and for those east of UTC is always positive.</div>','string','m-d-Y h:i A','m-d-Y h:i A','text','\n',1,1,'2010-08-12 17:19:14','','site','global',0);
